const w4 = @import("../wasm4.zig");
const std = @import("std");

const Point = @import("../Point.zig");
const Rect = @import("../Rect.zig");
const Controller = @import("../Controller.zig");

const Rhombus = @import("../Rhombus.zig");
const Player = @import("../Player.zig");

const Self = @This();

const Brazier = struct {
    pos: Point,
    exploding: u8 = 0,

    pub const wh = 16;
    pub const explode_time = 80;
};

boss: Rhombus = .{},
player: Player = .{
    .pos = .{ .x = 76, .y = 68 },
},

braziers: std.BoundedArray(Brazier, 3) = .{ .buffer = undefined },

pub fn update(self: *Self, controller: Controller) bool {
    self.player.update(controller, null);

    self.player.pos.x = std.math.clamp(self.player.pos.x, 0, 152);
    self.player.pos.y = std.math.clamp(self.player.pos.y, 0, 148);

    self.boss.update();

    // remove braziers when done exploding
    for (self.braziers.slice()) |*brazier, n| {
        if (brazier.exploding > 0) {
            brazier.exploding -= 1;

            if (brazier.exploding == 0) {
                _ = self.braziers.swapRemove(n);
                break;
            }
        }
    }

    // reset braziers when empty
    if (self.braziers.len == 0) {
        const seed: u32 = @intCast(u32, self.boss.area.x + self.boss.area.y) + self.boss.timer;
        var rng = std.rand.DefaultPrng.init(seed);
        const random = rng.random();

        while (self.braziers.len < 3) {
            var new = self.braziers.addOneAssumeCapacity();
            new.pos = Point{
                .x = random.intRangeLessThanBiased(i32, 0, 144),
                .y = random.intRangeLessThanBiased(i32, 0, 144),
            };
            new.exploding = 0;
        }
    }

    if (self.player.attacking > 0) {
        const hitbox = self.player.hitbox();
        // player triggers brazier
        for (self.braziers.slice()) |*brazier| {
            const rect = Rect{
                .x = brazier.pos.x,
                .y = brazier.pos.y,
                .w = Brazier.wh,
                .h = Brazier.wh,
            };

            if (rect.collides(hitbox)) {
                brazier.exploding = Brazier.explode_time;
            }
        }

        if (self.player.power_attack and self.boss.area.collides(hitbox)) {
            self.boss.take_damage(1);
        }
    } else {
        const hurtbox = self.player.to_rect();
        if (self.boss.area.collides(hurtbox)) {
            self.player.take_damage(2);
        }
    }

    // braziers damage boss
    for (self.braziers.constSlice()) |brazier| {
        if (brazier.exploding > 0) {
            const hitbox = Rect{
                .x = brazier.pos.x,
                .y = brazier.pos.y,
                .w = Brazier.wh,
                .h = Brazier.wh,
            };

            if (self.boss.area.collides(hitbox)) {
                self.boss.take_damage(2);
                break;
            }
        }
    }

    w4.DRAW_COLORS.* = 0x31;
    w4.blit(&boss_arena, 4, 5, boss_arena_width, boss_arena_height, boss_arena_flags);

    for (self.braziers.constSlice()) |brazier| {
        if (brazier.exploding > 0) {
            const flash = brazier.exploding & 0b10 == 0;
            w4.DRAW_COLORS.* = if (flash) 0x34 else 0x43;
            w4.oval(brazier.pos.x, brazier.pos.y, 16, 16);
        } else {
            w4.DRAW_COLORS.* = 0x12;
            w4.blit(&brazier_lit, brazier.pos.x, brazier.pos.y, 16, 16, 0);
        }
    }

    self.player.draw(Point.zero);

    self.boss.draw();

    return !self.boss.is_alive();
}

