const w4 = @import("../wasm4.zig");
const std = @import("std");

const Labyrinth = @import("Labyrinth.zig");
const Controller = @import("../Controller.zig");

showing_help: bool = false,
seed: i32 = 0x849,
seed_text_buffer: [8]u8 = "849     ".*,
seed_text_len: usize = 3,

pub fn update(self: *@This(), gamepad: Controller) bool {
    if (!self.showing_help) {
        // select seed and play!
        const x = gamepad.x_axis();
        if (x != 0) {
            self.seed +%= x;
            self.seed_text_len = std.fmt.formatIntBuf(&self.seed_text_buffer, @as(u32, @bitCast(self.seed)), 16, .upper, .{});
        } else if (gamepad.released.x) {
            // starting the game requires the maze to be generated before we set the game state
            // to labyrinth. return true; puts us in a cutscene, then the labyrinth.
            Labyrinth.maze.generate(@as(u32, @bitCast(self.seed)));
            return true;
        } else if (gamepad.released.y) {
            // help!
            self.showing_help = true;
        }

        // drawing game options play/seed select/help
        w4.DRAW_COLORS.* = 0x3241;
        w4.blit(&title_screen_sa, 20, 0, 120, 120, title_screen_sa_flags);

        w4.DRAW_COLORS.* = 0x13;
        w4.text("\x80  Play!", 18, 100);
        w4.text("\x84\x85 World", 18, 110);
        w4.text(self.seed_text_buffer[0..self.seed_text_len], 20 + 9 * 8, 110);

        w4.text("\x81  Help!", 18, 130);
    } else {
        // help screen
        w4.text("Roaches have keys!", 2, 2);
        w4.text("Find sacred flames!", 2, 12);
        w4.text("Hold to break rocks!", 2, 22);
        w4.text("Don't die!", 2, 42);

        w4.text("Press \x80", 80 - 7 * 4, 130);

        if (gamepad.released.x or gamepad.released.y) {
            self.showing_help = false;
        }
    }

    return false;
}
